mapper.select:
select *
from wechat_users a
where a.is_delete = 1 and a.open_id = #{openId}

mapper.update:
update wechat_users
set name = #{name}, id_card_type = #{idCardType}, id_card = #{idCard}, phone_number = #{phoneNumber},
organization_code = #{organizationCode}, status = #{status}, update_time = #{updateTime}
where open_id = #{openId}
